INSERT INTO `ocpizza`.`Recipe` (`idRecipe`, `recipe_details`, `id_product`) VALUES ('1', '\'c9talez la p\'e2te finement, garnissez-en la moiti\'e9 de d\'e9s de mozzarella, de sauce tomate, de champignons et de lani\'e8res de jambon. Laissez le bord net pour permettre de refermer la calzone. Saupoudrez d\'92origan, assaisonnez et arrosez d\'92un peu d\'92huile d\'92olive. Cassez l\'92oeuf d\'e9licatement et d\'e9posez-le au centre de la garniture. Humectez les bords de la pizza et repliez-la en pressant les bords. Conseil\'a0: pour un aspect plus joli, vous pouvez dorer le chausson de p\'e2te avec un peu de jaune d\'92oeuf d\'e9lay\'e9 dans de l\'92eau avant de mettre au four.', '1');\
INSERT INTO `ocpizza`.`Recipe` (`idRecipe`, `recipe_details`, `id_product`) VALUES ('2', 'Etalez la p\'e2te finement, garnissez avec votre cr\'e8me fraiche, la mozzarella puis le saumon en fines lamelles. Ajoutez quelques noix de persillade. Saupoudrez d\\'origan. Mettre au four.', '2');\
INSERT INTO `ocpizza`.`Recipe` (`idRecipe`, `recipe_details`, `id_product`) VALUES ('3', 'Etalez la p\'e2te finement, garnissez avec votre sauce tomate, la mozzarella, les poivrons, quelques morceaux de tomates fra\'eeches et d\\'artichaut. Saupoudrez d\\'herbes de Provence. Ajouter quelques olives. Mettre au four.', '3');\
INSERT INTO `ocpizza`.`Recipe` (`idRecipe`, `recipe_details`, `id_product`) VALUES ('4', 'Faire revenir les oignons tranch\'e9s en fines lamelles dans une po\'eale avec de l\\'huile d\\'olive. Faire cuire les steaks. En fin de cuisson ajouter le cheddar sur le steak pour qu il fonde. D\'e9posez la sauce, les oignons, la salade, les steaks avec les cheddar puis refermer le burger avec le couvercle.', '4');\
INSERT INTO `ocpizza`.`Recipe` (`idRecipe`, `recipe_details`, `id_product`) VALUES ('5', 'Etalez la p\'e2te finement, garnissez avec votre sauce tomate, la mozzarella. Saupoudrez d\\'origan. Mettre au four.', '9');\
INSERT INTO `ocpizza`.`Recipe` (`idRecipe`, `recipe_details`, `id_product`) VALUES ('6', 'Etalez la p\'e2te finement, garnissez avec votre sauce tomate, quelques tranches de jambon et de pommes de terre. Ajoutez un cuill\'e8re \'e0 soupe de cancoillotte. Mettre au four.', '11');\
